INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (1, 'Dilwale Dulhania Le Jayenge', 'Shah Rukh Khan, Kajol', 'A young couple, Raj and Simran, fall in love during a trip to Europe, but Simran is already promised to another man. Raj must win over her family and prove his love for her in a battle of tradition and romance.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (2, 'Sholay', 'Amitabh Bachchan, Dharmendra, Hema Malini, Amjad Khan', 'Two ex-convicts, Jai and Veeru, are hired by a retired police officer to capture a ruthless bandit, Gabbar Singh. The film is a mix of action, drama, and friendship set in a rural backdrop.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (3, 'Lagaan', 'Aamir Khan, Gracy Singh', 'In British India, a group of villagers challenge the British rulers to a game of cricket to avoid paying high taxes. This epic drama blends sports, colonial resistance, and unity in a historic context.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (4, 'Kabhi Khushi Kabhie Gham', 'Shah Rukh Khan, Amitabh Bachchan, Kajol, Hrithik Roshan', 'A family drama revolving around complex relationships, with themes of love, sacrifice, and reunion. The story focuses on a son who is disowned for marrying against his family''s wishes but later returns to heal old wounds.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (6, 'Taare Zameen Par', 'Aamir Khan, Darsheel Safary', 'The story of a dyslexic child, Ishaan, and his journey through childhood struggles and a compassionate teacher, played by Aamir Khan, who helps him realize his true potential in the world of art and creativity.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (7, '3 Idiots', 'Aamir Khan, R. Madhavan, Sharman Joshi', 'Three engineering students navigate the pressures of college life while challenging societal norms and expectations. The movie highlights the flaws in the education system and the importance of following one''s passion.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (8, 'Mughal-e-Azam', 'Dilip Kumar, Madhubala', 'The epic love story between Prince Salim and the courtesan Anarkali is set against the backdrop of the Mughal Empire. The film explores themes of love, sacrifice, and defiance against royal tyranny.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (5, 'Dil Chahta Hai', 'Aamir Khan, Saif Ali Khan, Akshaye Khanna', 'The film follows three inseparable childhood friends who, as adults, navigate love, relationships, and the challenges of growing up. It explores themes of friendship, commitment, and change.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (9, 'Zindagi Na Milegi Dobara', 'Hrithik Roshan, Farhan Akhtar, Abhay Deol', 'Three childhood friends go on a road trip to Spain, where each of them confronts personal fears, emotional baggage, and the meaning of life. The movie is a celebration of friendship and self-discovery.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (10, 'Jab We Met', 'Shahid Kapoor, Kareena Kapoor', 'A chance meeting between a heartbroken businessman and a free-spirited woman on a train journey leads to a romantic adventure. The film highlights themes of change, personal growth, and finding love when least expected.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (11, 'Baazigar', 'Shah Rukh Khan, Kajol, Shilpa Shetty', 'A young man, seeking revenge for his family''s death, uses a girl''s love to destroy her family. The film is a thriller about love, betrayal, and revenge.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (12, 'Kuch Kuch Hota Hai', 'Shah Rukh Khan, Kajol, Rani Mukerji', 'A college student falls in love with his best friend but is unaware that she has secretly loved him for years. After her tragic death, he realizes his feelings for her and their young daughter helps reunite them.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (14, 'Dil Se', 'Shah Rukh Khan, Manisha Koirala', 'A passionate reporter becomes embroiled in the dangerous world of terrorism while falling in love with a woman involved in a deadly plot to assassinate government leaders.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (15, 'Aashiqui 2', 'Aditya Roy Kapur, Shraddha Kapoor', 'A talented singer with a self-destructive streak falls in love with an aspiring singer. As her career flourishes, he battles his addiction and struggles to find redemption.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (16, 'Chakde! India', 'Shah Rukh Khan', 'A former hockey player becomes the coach of the Indian women''s national hockey team. The film explores themes of teamwork, redemption, and national pride.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (17, 'Barfi!', 'Ranbir Kapoor, Priyanka Chopra, Ileana D''Cruz', 'A mute and deaf man falls in love with two women at different stages of his life, overcoming his disabilities and societal prejudices. A heartwarming story of love, innocence, and acceptance.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (18, 'Rock On!!', 'Arjun Rampal, Farhan Akhtar, Prachi Desai', 'Four friends reunite after years and revive their old rock band, facing past regrets, the impact of their choices, and their quest for redemption through music.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (19, 'Kabir Singh', 'Shahid Kapoor, Kiara Advani', 'A brilliant but emotionally unstable surgeon goes through a turbulent relationship with his college sweetheart. The movie explores his emotional struggles, addiction, and destructive love.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (20, 'Queen', 'Kangna Ranaut, Rajkummar Rao', 'A young woman embarks on a solo honeymoon trip to Europe after her marriage falls apart. She discovers her independence and learns to embrace her new life and herself.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (21, 'Paa', 'Amitabh Bachchan, Vidya Balan', 'A young boy suffers from a rare disease that causes rapid aging. His mother and father, a politician, struggle with their emotions as they try to raise him and accept his condition.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (22, 'Chhichhore', 'Sushant Singh Rajput, Shraddha Kapoor', 'A group of middle-aged men, reflecting on their college days, rekindle their friendships to help one of their sons deal with failure, teaching him the importance of enjoying life and not fearing failure.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (23, 'Jab Tak Hai Jaan', 'Shah Rukh Khan, Katrina Kaif, Anushka Sharma', 'A soldier who survives an assassination attempt falls in love with a documentary filmmaker while secretly dealing with a promise he made years ago to another woman.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (24, 'Bajirao Mastani', 'Ranveer Singh, Deepika Padukone, Priyanka Chopra', 'The historical epic revolves around the love story of Maratha warrior Bajirao and his Muslim concubine Mastani, set against the backdrop of political conflicts and family feuds.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (25, 'Mere Brother Ki Dulhan', 'Imran Khan, Katrina Kaif, Ali Zafar', 'A man sets out to find a bride for his brother but ends up falling in love with her himself, leading to a series of comedic and emotional twists.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (26, 'Drishyam', 'Ajay Devgn, Tabu', 'A common man goes to great lengths to protect his family after they inadvertently commit a crime. The film explores themes of justice, truth, and deception.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (27, 'Tanu Weds Manu', 'R. Madhavan, Kangna Ranaut', 'A man travels from London to India to marry a woman chosen by his parents but instead falls in love with a free-spirited girl who is already engaged to another.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (28, 'Dangal', 'Aamir Khan, Fatima Sana Shaikh, Sanya Malhotra', 'The inspiring true story of a father who trains his daughters to become world-class wrestlers, challenging gender norms and fighting for their success in a male-dominated sport.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (29, 'Kabhi Alvida Naa Kehna', 'Shah Rukh Khan, Abhishek Bachchan, Rani Mukerji', 'Two married couples face the complexities of infidelity and emotional turmoil, challenging the notion of love, commitment, and marital bonds in contemporary society.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (31, 'Jolly LLB', 'Arshad Warsi, Boman Irani', 'A struggling lawyer takes on a high-profile case involving a corrupt politician and a case of police brutality. The film satirizes the Indian judicial system while showing the journey of an underdog lawyer.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (32, 'Shuddh Desi Romance', 'Sushant Singh Rajput, Parineeti Chopra, Vaani Kapoor', 'A young man in a live-in relationship struggles with commitment issues and explores his own feelings of love and marriage, against the backdrop of modern relationships in India.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (33, 'Ki & Ka', 'Kareena Kapoor, Arjun Kapoor', 'A young couple challenges traditional gender roles, as the wife works while the husband takes care of the home. The film explores the modern relationships in a patriarchal society.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (34, 'Haider', 'Shahid Kapoor, Tabu, Shraddha Kapoor', 'A modern-day adaptation of Shakespeare''s "Hamlet," set against the backdrop of the Kashmir conflict. A man returns home to avenge his father''s death and uncovers shocking truths.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (35, 'Gully Boy', 'Ranveer Singh, Alia Bhatt', 'A young man from Mumbai''s slums rises to fame as a rapper, using his lyrics to express his struggles with his family, society, and his dreams of a better life.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (36, 'Fukrey', 'Pulkit Samrat, Varun Sharma, Ali Fazal', 'Four young friends, looking to make easy money, get involved in a chaotic and hilarious situation involving a local gangster and a corrupt politician.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (37, 'Makkhi', 'Sudeep, Samantha Ruth Prabhu', 'A man is reincarnated as a fly after being murdered. He seeks revenge on his killer and saves his love interest in a unique story about love and justice.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (38, 'Bhaag Milkha Bhaag', 'Farhan Akhtar, Sonam Kapoor', 'The inspiring true story of Milkha Singh, an Indian athlete who overcomes immense personal and societal challenges to become a world-class sprinter, overcoming loss and hardship.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (39, 'Tanu Weds Manu Returns', 'Kangana Ranaut, R. Madhavan', 'The sequel to Tanu Weds Manu, where the protagonists navigate the complexities of marriage and love as Tanu returns to her ex-husband''s life, bringing chaos and humor.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (40, 'Housefull', 'Akshay Kumar, Deepika Padukone, Lara Dutta', 'A comedy of errors involving mistaken identities, love, and family drama, as a man with a series of bad luck entangles himself in a web of lies while trying to find his true love.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (41, 'Housefull 2', 'Akshay Kumar, John Abraham, Asin, Jacqueline Fernandez', 'A madcap comedy involving mistaken identities, family drama, and a tangled web of lies as a group of eccentric characters try to find their wealth while facing chaos and confusion.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (42, 'Raees', 'Shah Rukh Khan, Mahira Khan, Nawazuddin Siddiqui', 'A young man from Gujarat rises to power in the criminal underworld while maintaining his moral compass. A gripping tale of ambition, power, and betrayal in a corrupt system.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (45, 'Dostana', 'Abhishek Bachchan, John Abraham, Priyanka Chopra', 'Two men pretend to be a gay couple to share an apartment with a beautiful woman, leading to hilarious situations. The film addresses themes of love, friendship, and identity with humor and charm.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (46, 'Chennai Express', 'Shah Rukh Khan, Deepika Padukone', 'A man on a journey to immerse his late wife’s ashes accidentally finds himself in the midst of a gang rivalry while falling for a beautiful woman. A thrilling romantic comedy set in the backdrop of South India.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (48, 'Madhuri Dixit Nene', 'Madhuri Dixit Nene', 'The story of a dancer who battles societal pressures, seeking love and acceptance in the competitive world of dance while trying to make a name for herself.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (49, 'Andhadhun', 'Ayushmann Khurrana, Tabu, Radhika Apte', 'A blind pianist becomes involved in a web of crime, mystery, and murder after witnessing a death, leading him into an intricate and darkly comic adventure. A film about deception, suspense, and survival.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (50, 'Raat Akeli Hai', 'Nawazuddin Siddiqui, Radhika Apte', 'A small-town cop investigates the murder of a wealthy family member on the night of his wedding, uncovering secrets and lies while navigating a web of intrigue, suspense, and betrayal.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (53, 'Bhootnath', 'Amitabh Bachchan, Shah Rukh Khan', 'A ghost, who has been haunting a house for years, forms an unlikely friendship with a young boy and helps him defeat the evil forces trying to take over the house. A heartwarming, supernatural family drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (54, 'The Lunchbox', 'Irrfan Khan, Nimrat Kaur, Nawazuddin Siddiqui', 'A mistaken lunch delivery leads to a correspondence between two strangers who find an unexpected bond through their lunchbox notes. A poignant and quiet love story about loneliness and connection.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (55, 'Mardaani', 'Rani Mukerji', 'A tough and determined police officer investigates a human trafficking ring, uncovering dark secrets and risking everything to stop the illegal trade. A thrilling and empowering film about justice.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (56, 'Bajrangi Bhaijaan', 'Salman Khan, Kareena Kapoor, Nawazuddin Siddiqui', 'A man embarks on a journey to reunite a mute Pakistani girl with her family across the border, despite political tensions and personal challenges. A heartwarming story of love and humanity beyond borders.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (59, 'Dhamaal', 'Riteish Deshmukh, Arshad Warsi, Sanjay Dutt', 'A group of friends gets entangled in a series of hilarious adventures while attempting to find a hidden treasure. A comedy film that brings together a mix of colorful characters and outrageous situations.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (60, 'Sultan', 'Salman Khan, Anushka Sharma', 'A wrestler from Haryana overcomes numerous obstacles and battles personal demons to become a national champion. The film explores themes of redemption, self-worth, and overcoming adversity.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (62, 'Kahaani', 'Vidya Balan', 'A pregnant woman travels to Kolkata in search of her missing husband, only to uncover a deeper conspiracy that involves the police, a secret agent, and a series of dark truths.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (64, 'Shubh Mangal Zyada Saavdhan', 'Ayushmann Khurrana, Jitendra Kumar', 'A socially relevant romantic comedy that explores the struggles of a gay couple coming out to their conservative families and fighting against societal norms to live their love openly.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (66, 'Race 3', 'Salman Khan, Jacqueline Fernandez, Anil Kapoor', 'The third installment in the Race franchise, filled with action, betrayal, and intrigue. The movie focuses on a family caught in a web of revenge, power struggles, and deception.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (67, 'Ae Dil Hai Mushkil', 'Ranbir Kapoor, Anushka Sharma, Aishwarya Rai', 'A young man falls in love with a woman who is not emotionally available, and they go through various stages of love, friendship, and heartbreak. The movie explores unrequited love and self-discovery.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (72, 'PK', 'Aamir Khan, Anushka Sharma', 'A humanoid alien arrives on Earth and questions the beliefs and customs of humans in a humorous yet philosophical way. The film explores the concepts of religion, society, and human nature through the eyes of an outsider.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (73, 'Kahaani 2', 'Vidya Balan, Arjun Rampal', 'A woman with a hidden past goes on the run after being accused of a crime she didn’t commit, all while trying to protect her daughter from a dark secret. A suspense thriller with an emotional twist.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (75, 'Tamasha', 'Ranbir Kapoor, Deepika Padukone', 'A man meets a woman while traveling abroad, and they agree to pretend to be someone else. Their story unfolds with themes of self-discovery, love, and finding one’s true identity amid societal pressures.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (78, 'Goliyon Ki Raasleela Ram-Leela', 'Ranveer Singh, Deepika Padukone', 'A passionate love story set in the backdrop of violence and family feuds, as two lovers from rival families find their love tested by societal pressures. A modern retelling of Shakespeare''s Romeo and Juliet.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (79, 'Chhapaak', 'Deepika Padukone, Vikrant Massey', 'Based on the real-life story of acid attack survivor Laxmi Agarwal, the movie tells the powerful story of her fight for justice, as well as her personal and emotional journey of healing.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (80, 'Dilwale', 'Shah Rukh Khan, Kajol, Varun Dhawan, Kriti Sanon', 'A story of love, revenge, and family, set against the backdrop of a crime world. The film explores the story of two brothers who are caught between their love for each other and the love for their significant others.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (85, 'Piku', 'Amitabh Bachchan, Deepika Padukone, Irrfan Khan', 'The story of a father-daughter duo and their relationship dynamics as they embark on a road trip to Kolkata. The film delicately explores themes of aging, love, and the complexities of family life.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (87, 'Dhoom 2', 'Hrithik Roshan, Aishwarya Rai, Abhishek Bachchan', 'A sequel to the Dhoom franchise, this film follows a master thief and a cop as they face off in a high-stakes game of cat and mouse. The film is filled with action, suspense, and drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (88, 'Don 2', 'Shah Rukh Khan, Priyanka Chopra, Boman Irani', 'The sequel to Don, where the king of the underworld seeks to dominate Europe’s criminal empire. A thrilling action-packed film full of mind games, twists, and betrayal.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (90, 'Student of the Year', 'Varun Dhawan, Alia Bhatt, Sidharth Malhotra', 'Three high school friends compete in a prestigious student of the year competition, navigating love, rivalry, and friendship in their journey to win the coveted title.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (91, 'Ek Villain', 'Sidharth Malhotra, Shraddha Kapoor, Riteish Deshmukh', 'A man seeks revenge on the person who murdered his wife, only to discover the shocking truths behind her death. The film is an intense thriller about love, loss, and redemption.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (93, 'Padmaavat', 'Deepika Padukone, Shahid Kapoor, Ranveer Singh', 'The epic tale of a Rajput queen, Padmavati, who is caught in a battle of honor, love, and betrayal with the ruthless Sultan Alauddin Khilji. A grand historical drama filled with emotion and action.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (98, 'Tumhari Sulu', 'Vidya Balan, Manav Kaul, Neha Dhupia', 'A middle-class housewife discovers her hidden talent for radio jockeying and embarks on a career in a completely new domain. The film explores female empowerment, personal growth, and ambition.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (99, 'Jab Harry Met Sejal', 'Shah Rukh Khan, Anushka Sharma', 'A heartbroken man and a confused woman embark on a journey through Europe to find her lost engagement ring. Their adventures lead them to discover love, self-awareness, and the truth about their own desires.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (104, 'Airlift', 'Akshay Kumar, Nimrat Kaur', 'Based on true events, the film tells the story of the largest human evacuation operation carried out by India during the Gulf War to rescue Indians stranded in Kuwait. A tale of heroism and survival.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (105, 'Badhaai Ho', 'Ayushmann Khurrana, Neena Gupta, Gajraj Rao', 'A middle-aged couple unexpectedly find themselves expecting a child, leading to embarrassing and awkward situations. The film humorously explores family dynamics, societal expectations, and the taboo of aging parents having a child.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (106, 'Tumbbad', 'Sohum Shah, Jyoti Malshe', 'A dark fantasy set in a cursed village, where a man searches for a hidden treasure and uncovers a terrifying secret. The film blends horror, mythology, and a deep philosophical exploration of greed and obsession.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (109, 'Veere Di Wedding', 'Sonam Kapoor, Kareena Kapoor, Swara Bhaskar, Shikha Talsania', 'A modern-day friendship drama revolving around four women as they navigate relationships, marriage, and self-discovery, breaking societal norms and celebrating womanhood.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (110, 'Secret Superstar', 'Zaira Wasim, Aamir Khan', 'A young girl with dreams of becoming a singer faces her conservative family’s opposition. The film showcases her journey to fame under an alias and the challenges of pursuing her passion despite societal restrictions.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (111, 'Dabangg 2', 'Salman Khan, Sonakshi Sinha', 'The sequel to Dabangg, where Chulbul Pandey is back as the fearless and witty police officer who confronts a powerful criminal and deals with his personal life. The film is packed with action, humor, and family drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (112, 'Rang De Basanti', 'Aamir Khan, Siddharth, Sharman Joshi', 'A group of university students is inspired to become politically active after they portray freedom fighters in a documentary. The film explores youth activism, patriotism, and the consequences of inaction.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (115, 'Dil Dhadakne Do', 'Ranveer Singh, Priyanka Chopra, Anil Kapoor', 'A dysfunctional family embarks on a luxury cruise for a celebration, unraveling family secrets and misunderstandings while learning about each other’s desires and dreams. A film about family, love, and self-discovery.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (117, 'Chupke Chupke', 'Dharmendra, Sharmila Tagore, Amitabh Bachchan', 'A newlywed couple orchestrates a hilarious plan to play pranks on their family and friends during their honeymoon. The movie is a light-hearted comedy about love, deception, and family relationships.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (121, 'Dhoom 3', 'Aamir Khan, Katrina Kaif, Abhishek Bachchan', 'The third installment in the Dhoom series, featuring a circus performer turned villain who takes on a police officer and his team. Packed with action, twists, and suspense, the movie keeps you on the edge of your seat.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (124, 'Jolly LLB 2', 'Akshay Kumar, Huma Qureshi', 'A lawyer challenges a corrupt system after the death of an innocent man, leading to a thrilling legal battle with humorous undertones. The movie is a satire on India’s legal and judicial system.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (125, 'Pardes', 'Shah Rukh Khan, Mahima Chaudhary, Amrish Puri', 'A young man goes to America to bring back the daughter of a traditional Indian family. The film explores themes of culture shock, traditional values, and the clash between Eastern and Western cultures.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (126, 'Madhuri Dixit', 'Madhuri Dixit', 'The story of a dancer who battles societal pressures, seeking love and acceptance in the competitive world of dance while trying to make a name for herself.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (127, 'Krrish 3', 'Hrithik Roshan, Kangna Ranaut, Vivek Oberoi', 'The third installment in the Krrish series, where the superhero Krrish faces off against a dangerous scientist bent on creating a mutant army. The film is a mix of action, fantasy, and family drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (129, 'Dabangg 3', 'Salman Khan, Sonakshi Sinha', 'Chulbul Pandey faces his old enemies and deals with a new criminal while navigating personal and family issues. The movie is filled with action, comedy, and emotional moments, set in a larger-than-life narrative.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (131, 'Meri Pyaari Bindu', 'Ayushmann Khurrana, Parineeti Chopra', 'A love story between a writer and a singer, where their relationship evolves over the years. The film captures the joy, sorrow, and nostalgia of first love, blended with an emotional journey of self-discovery.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (133, 'Ludo', 'Abhishek Bachchan, Pankaj Tripathi, Rajkummar Rao, Sanya Malhotra', 'A quirky film that tells four interconnected stories of love, fate, and redemption, where the characters’ lives intersect like the dice rolls of the classic game of Ludo. A dark comedy exploring human emotions and unpredictability.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (134, 'Pad Man', 'Akshay Kumar, Radhika Apte, Sonam Kapoor', 'Inspired by the true story of a man who created a low-cost sanitary pad-making machine to help women in rural India. The film addresses taboo topics like menstruation, hygiene, and women’s empowerment.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (136, 'Raazi', 'Alia Bhatt, Vicky Kaushal', 'A young Indian woman is recruited as a spy during the 1971 India-Pakistan war and is tasked with gathering intelligence to prevent an impending attack. The film explores love, patriotism, and sacrifice against the backdrop of war.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (139, 'Dhamaka', 'Kartik Aaryan, Mrunal Thakur', 'A news anchor in a crisis turns into a media sensation when he finds himself caught up in a terrorist threat, broadcasting live as tensions rise. The movie deals with media sensationalism, morality, and the blurred lines between truth and spectacle.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (142, 'Khamoshi: The Musical', 'Salman Khan, Manisha Koirala', 'A love story between a mute and deaf woman and a man who finds himself drawn to her world of silence. The film explores themes of communication, understanding, and the struggles of people with disabilities.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (143, 'Kedarnath', 'Sushant Singh Rajput, Sara Ali Khan', 'A love story set against the backdrop of the 2013 Uttarakhand floods. A Muslim man and a Hindu woman fall in love but are separated by religious and social divides, leading to a tale of sacrifice and love.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (144, 'Highway', 'Alia Bhatt, Randeep Hooda', 'A young woman is kidnapped, and through the journey across India, she finds a sense of freedom and self-discovery, despite the circumstances. The movie explores themes of captivity, freedom, and emotional healing.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (148, 'Lage Raho Munna Bhai', 'Sanjay Dutt, Vidya Balan, Arshad Warsi', 'Munna Bhai, a local gangster, starts learning the teachings of Mahatma Gandhi to win the heart of a woman. A comedy about love, social change, and the importance of non-violence and truth.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (150, 'Dabangg', 'Salman Khan, Sonakshi Sinha', 'Chulbul Pandey, a fearless and witty police officer, battles corruption and crime while navigating family issues. A mix of action, humor, and drama, the film became a massive hit due to its iconic characters and dialogue delivery.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (164, 'Pati Patni Aur Woh', 'Kartik Aaryan, Bhumi Pednekar, Ananya Panday', 'A married man starts an affair with a colleague, leading to comic misunderstandings and complications. A light-hearted film about relationships, infidelity, and the comedic side of human emotions.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (166, 'Stree', 'Rajkummar Rao, Shraddha Kapoor', 'A small town is haunted by the spirit of a woman who kidnaps men at night. The film blends horror and comedy as a group of friends tries to uncover the truth behind the spooky legend, with a unique twist at the end.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (168, 'Shershaah', 'Sidharth Malhotra, Kiara Advani', 'The biographical film tells the story of Captain Vikram Batra, a soldier martyred during the Kargil War. The film highlights bravery, patriotism, and the sacrifices of the armed forces during one of India’s most challenging military operations.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (170, 'Bhoot Part One: The Haunted Ship', 'Vicky Kaushal, Bhumi Pednekar', 'A paranormal thriller revolving around a haunted ship that drifts into Mumbai’s coastline. A man’s investigation into the terrifying mystery unravels dark secrets. The film mixes horror with mystery and suspense.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (178, 'Hawaizaada', 'Ayushmann Khurrana, Pallavi Sharda', 'Based on the life of the unsung inventor of India’s first flying machine, the film explores the spirit of innovation and the struggles of a young man in pre-independence India to bring his dreams to life.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (180, 'Padmavat', 'Deepika Padukone, Shahid Kapoor, Ranveer Singh', 'A historical epic based on the poem "Padmavat" by Malik Muhammad Jayasi, the film narrates the story of Queen Padmavati and her unwavering devotion to her kingdom as she confronts Sultan Alauddin Khilji’s invasion.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (182, 'Hera Pheri', 'Akshay Kumar, Sunil Shetty, Paresh Rawal', 'Three hapless individuals find themselves embroiled in a series of comic misadventures while trying to make quick money. One of the most beloved comedy films in Bollywood, featuring iconic performances and memorable humor.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (221, 'Coolie No. 1', 'Govinda, Karisma Kapoor', 'A comedy film where a simple, fun-loving man masquerades as a wealthy businessman to win the heart of a rich girl, leading to hilarious complications. The film features slapstick humor and showcases Govinda’s impeccable comic timing.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (222, 'Hero No. 1', 'Govinda, Karisma Kapoor', 'A comedy of errors where a man takes on multiple identities to impress a rich family and win the love of a woman. The film is a classic Govinda comedy with plenty of misunderstandings, romance, and humor.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (223, 'Haseena Maan Jayegi', 'Govinda, Sanjay Dutt, Karisma Kapoor', 'Two mischievous friends enter a lavish lifestyle through deception and disguise. The film blends comedy and romance as the characters find themselves entangled in a series of misunderstandings.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (224, 'Bade Miyan Chote Miyan', 'Amitabh Bachchan, Govinda, Raveen Tondon', 'A comedy-action film where two cops (Amitabh Bachchan and Govinda) with completely different personalities team up to solve a case. The film is known for its hilarious dialogues and the iconic pairing of Bachchan and Govinda.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (226, 'Ankhen', 'Govinda, Raj Babbar, Chunkey Pandey', 'A fast-paced comedy about a man who disguises himself to hide from the police while getting involved in a series of chaotic situations. The movie showcases Govinda’s versatile comic flair in an ensemble cast.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (227, 'Raja Babu', 'Govinda, Karisma Kapoor', 'A comedy film where an innocent man who is adopted by a rich family finds himself at odds with his biological parents, leading to hilarious situations. The film became a major hit because of its humor and Govinda’s charm.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (228, 'Saajan Chale Sasural', 'Govinda, Karisma Kapoor, Tabu', 'A man leads a double life to keep his marriage intact, but his lies start to unravel as his two wives unknowingly meet. A classic Govinda comedy filled with situational humor and mistaken identities.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (229, 'Jodi No. 1', 'Govinda, Sanjay Dutt', 'Two small-time crooks, who are mistaken for notorious criminals, find themselves caught up in a web of mistaken identities and hilarious situations. The film is a typical Govinda comedy that balances humor and action.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (230, 'Partner', 'Salman Khan, Govinda, Katrina Kaif', 'A romantic comedy where a charming and witty man (Govinda) helps his friend win over the woman of his dreams. The film is filled with comic situations, hilarious one-liners, and strong performances by Govinda.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (231, 'Do Knot Disturb', 'Govinda, Riteish Deshmukh, Lara Dutta', 'A comedy about a married man who gets entangled in lies when he tries to hide his extramarital affair from his wife. The film is a laugh riot, with Govinda’s character at the center of the confusion and humor.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (232, 'Bhagam Bhag', 'Akshay Kumar, Govinda, Lara Dutta', 'A chaotic comedy film where two comedians become unwittingly involved in a murder mystery. Govinda brings his unique brand of humor and timing in this fast-paced comedy.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (233, 'Salaam-e-Ishq', 'Salman Khan, Priyanka Chopra, Govinda', 'The film tells the stories of six couples, with one of the plotlines revolving around the romantic and comedic journey of a couple trying to rekindle their love. Govinda shines in his role with humor and heart.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (236, 'Maharaja', 'Govinda, Manisha Koirala', 'A man who is mistaken for a royal heir navigates a web of deception and intrigue while falling in love with a woman who holds a dark secret. A blend of romance, comedy, and drama with Govinda in a unique role.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (251, 'Black', 'Rani Mukerji, Amitabh Bachchan', 'The inspiring story of a deaf and blind girl, who, with the help of a teacher, fights to break the shackles of her disabilities and lead an independent life. Rani’s performance as Michelle McNally received critical acclaim.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (254, 'Veer-Zaara', 'Shah Rukh Khan, Rani Mukerji', 'The epic love story between an Indian Air Force officer and a Pakistani woman, set against the backdrop of political tensions and familial obligations. Rani Mukerji portrays the role of a determined lawyer fighting for love and justice.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (255, 'Ta Ra Rum Pum', 'Rani Mukerji, Saif Ali Khan', 'A family drama where a successful car racer’s career is ruined in an accident, and his family struggles to cope with financial hardship. Rani Mukerji’s character supports her family with love and perseverance.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (256, 'Bunty Aur Babli', 'Abhishek Bachchan, Rani Mukerji', 'Two small-town dreamers, who team up and embark on a life of crime, creating havoc across India with their innovative cons. Rani plays Babli, the witty and ambitious partner in crime. The film is a fun-filled, adventurous ride.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (257, 'Chalte Chalte', 'Shah Rukh Khan, Rani Mukerji', 'A romantic drama about a married couple facing difficulties in their relationship. The film explores themes of love, sacrifice, and misunderstandings, with Rani Mukerji delivering a heartfelt performance.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (258, 'Hichki', 'Rani Mukerji', 'A woman suffering from Tourette Syndrome takes up a teaching job at a school and faces challenges with her students and colleagues. Despite her condition, she is determined to inspire her students and overcome societal prejudices.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (259, 'Hum Tum', 'Saif Ali Khan, Rani Mukerji', 'A romantic comedy where two people meet several times over the years, with each encounter revealing new emotions. The film is a delightful exploration of love, misunderstandings, and fate, with Rani Mukerji playing the charming Radhika.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (261, 'Laaga Chunari Mein Daag', 'Rani Mukerji, Abhishek Bachchan', 'A woman who becomes a sex worker to support her family is forced to hide her identity from her own family. Rani Mukerji’s performance in this poignant and emotionally charged drama was highly praised.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (262, 'Ghulam', 'Aamir Khan, Rani Mukerji', 'A suspenseful drama about a man who must choose between his loyalty to a local gangster and doing the right thing. Rani Mukerji plays a strong supporting role as Aamir Khan’s love interest.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (263, 'Raja Ki Aayegi Baraat', 'Rani Mukerji, Shahrukh Khan', 'A young woman, after being raped, has to face societal judgment and her own personal struggle for justice. The film explores themes of revenge, forgiveness, and societal pressure. Rani Mukerji’s performance is both brave and emotional.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (264, 'Aiyya', 'Rani Mukerji, Prithviraj Sukumaran', 'A quirky romantic comedy where a middle-class woman becomes obsessed with her new neighbor, a man who is not only attractive but mysterious as well. The film explores love and infatuation in a light-hearted, offbeat way.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (265, 'Babul', 'Amitabh Bachchan, Hema Malini, Rani Mukerji', 'The story of a father who is determined to help his widowed daughter-in-law find happiness again. The film addresses the social stigma surrounding widows and second marriages in India, with Rani Mukerji portraying a sensitive and emotional role.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (284, 'Ra.One', 'Shah Rukh Khan, Kareena Kapoor, Arjun Rampal', 'A science fiction action film where a video game villain comes to life and terrorizes the real world. Kareena Kapoor plays the role of the protagonist’s wife, bringing strength and grace to her character in this high-tech thriller.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (285, 'Bodyguard', 'Salman Khan, Kareena Kapoor', 'A romantic action-comedy where a bodyguard falls in love with his employer, leading to misunderstandings and emotional turmoil. Kareena Kapoor plays the sweet and strong lead role, balancing both comedy and romance.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (287, 'Chup Chup Ke', 'Shahid Kapoor, Kareena Kapoor', 'A light-hearted romantic comedy where a man, trying to escape from his debts, is forced to impersonate a mute person and ends up falling in love. Kareena Kapoor plays the role of a woman who becomes involved in his chaotic world.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (288, 'Heroine', 'Kareena Kapoor, Arjun Rampal', 'A drama about a successful actress who struggles with fame, love, and the pressure of the entertainment industry. Kareena Kapoor plays the central character, portraying the vulnerability and strength of a woman in the spotlight.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (290, 'Hum Aapke Hain Koun..!', 'Salman Khan, Madhuri Dixit', 'The story revolves around the bond between a family and the relationship between two siblings, focusing on love, sacrifice, and family traditions. The movie features vibrant songs and is a celebration of family values.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (293, 'Rangeela', 'Aamir Khan, Urmila Matondkar, Jackie Shroff', 'A young woman from the slums dreams of becoming a movie star. Her life takes a turn when she is torn between her love for a man and her ambition, all while dealing with the glamour world.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (294, 'Jo Jeeta Wohi Sikandar', 'Aamir Khan, Ayesha Jhulka, Mamik Singh', 'A college rivalry turns into a battle for honor and love. The movie focuses on friendship, competition, and the spirit of sportsmanship, with a central theme of growing up.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (295, 'Darr', 'Shah Rukh Khan, Juhi Chawla, Sunny Deol', 'A man becomes obsessed with a woman he loves and will go to any lengths to keep her. The movie explores themes of obsession, love, and fear in a thrilling way.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (296, 'Andaz Apna Apna', 'Aamir Khan, Salman Khan, Raveen Tondon, Karisma Kapoor', 'Two slackers compete to win the heart of a wealthy heiress, but things get complicated as they inadvertently get involved in a bigger plot of crime. A classic slapstick comedy with memorable characters.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (297, 'Hum Dil De Chuke Sanam', 'Salman Khan, Aishwarya Rai, Ajay Devgn', 'A woman torn between the man she loves and her husband embarks on a journey of self-discovery and understanding the meaning of love and sacrifice. A passionate and emotional love story set to beautiful music.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (302, 'Munna Bhai M.B.B.S.', 'Sanjay Dutt, Arshad Warsi, Gracy Singh', 'A gangster, Munna Bhai, embarks on a mission to become a doctor to fulfill his father’s last wish. Along with his sidekick Circuit, he causes chaos in the hospital but ultimately changes lives with his unique approach to medicine.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (305, 'Don', 'Shah Rukh Khan, Priyanka Chopra, Boman Irani', 'A remake of the classic film, Don is a story of a ruthless gangster who is killed, but another man takes his identity to infiltrate the gang and bring justice. A mix of action, suspense, and drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (306, 'Om Shanti Om', 'Shah Rukh Khan, Deepika Padukone, Arjun Rampal', 'A film that blends reincarnation, romance, and revenge. Om, a junior artist, is murdered by a producer but is reincarnated and seeks revenge on those who wronged him in his previous life.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (310, 'Chak De! India', 'Shah Rukh Khan, Vidya Malvade, Sagarika Ghatge', 'A former hockey player, Kabir Khan, becomes the coach of the Indian women’s national hockey team and leads them to victory, overcoming prejudices and personal challenges along the way. The film is about redemption, teamwork, and national pride.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (312, 'Guru', 'Abhishek Bachchan, Aishwarya Rai, Mithun Chakraborty', 'The story of a small-town boy, Guru, who rises to power as a business tycoon, overcoming struggles and obstacles. It is an inspiring tale of ambition, hard work, and the pursuit of dreams.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (313, 'My Name is Khan', 'Shah Rukh Khan, Kajol, Jimmy Sheirgill', 'A man with Asperger syndrome embarks on a journey across America to meet the president and tell him that “My name is Khan, and I am not a terrorist.” The film explores themes of racism, love, and human compassion.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (318, 'Bhool Bhulaiyaa', 'Akshay Kumar, Vidya Balan, Shiney Ahuja', 'A psychological horror-comedy about a man who visits his ancestral home with his wife, only to uncover a dark, supernatural mystery involving a troubled past and a haunted spirit.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (319, 'Love Aaj Kal', 'Saif Ali Khan, Deepika Padukone, Rishi Kapoor', 'A contemporary romantic drama about a young couple dealing with the complexities of modern relationships, while reflecting on the past love story of two people from different generations.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (322, 'Jodhaa Akbar', 'Hrithik Roshan, Aishwarya Rai, Nitish Bharadwaj', 'A historical epic about the love story between the Mughal Emperor Akbar and the Rajput princess Jodhaa. The movie explores political intrigue, royal duties, and the blending of cultures and religions in medieval India.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (323, 'Wake Up Sid', 'Ranbir Kapoor, Konkona Sen Sharma, Anupam Kher', 'A coming-of-age story about Sid, a carefree young man who, after failing his exams, learns to take responsibility for his life. With the help of a new friend, he begins to realize his potential and the value of hard work and relationships.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (329, 'Ajab Prem Ki Ghazab Kahani', 'Ranbir Kapoor, Katrina Kaif, Upen Patel', 'A quirky romantic comedy about a man, Prem, who falls in love with a woman, Jenny, who is already in love with someone else. The film is full of humorous misunderstandings, love, and friendship.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (332, 'Kaminey', 'Shahid Kapoor, Priyanka Chopra, Amol Gupte', 'A thriller about two twin brothers, one a simple man and the other a ruthless gangster, who find themselves caught in a web of crime, drugs, and betrayal. The film mixes dark humor, action, and suspense.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (335, 'Mujhse Dosti Karoge!', 'Shah Rukh Khan, Rani Mukerji, Hrithik Roshan', 'A love triangle between childhood friends, where one secretly loves the other, but the other falls in love with someone else. The film explores love, friendship, and the complexities of unspoken emotions.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (337, 'Pyaar Ka Punchnama', 'Kartik Aaryan, Nushrat Bharucha, Sonnalli Seygall', 'A comedy about the complicated lives of three young men who are frustrated by their relationships with women. The film explores modern relationships and the clash between expectations and reality in love.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (340, 'Ghajini', 'Aamir Khan, Asin, Jiah Khan', 'A man with short-term memory loss seeks revenge on the people who murdered his fiancée. The film is a high-octane thriller with a mix of action, romance, and psychological drama.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (351, 'Taal', 'Aishwarya Rai, Akshaye Khanna, Anil Kapoor', 'A young woman from a rural background dreams of becoming a famous singer, but she faces many hurdles as she falls in love with two men. The movie explores love, ambition, and the clash between tradition and modernity.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (352, 'Karan Arjun', 'Shah Rukh Khan, Salman Khan, Kajol, Rakesh Roshan', 'Two brothers are reincarnated to avenge their murder and the injustice that their family faced. The movie combines action, drama, and supernatural elements in a thrilling revenge story.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (353, 'Phir Hera Pheri', 'Akshay Kumar, Sunil Shetty, Paresh Rawal', 'The sequel to the comedy "Hera Pheri," it follows the misadventures of Raju, Shyam, and Baburao as they get entangled in a new set of hilarious and chaotic circumstances. A hilarious mix of comedy, confusion, and misunderstandings.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (354, 'Raja Hindustani', 'Aamir Khan, Karisma Kapoor, Johnny Lever', 'A wealthy woman falls in love with a taxi driver, but their love faces numerous obstacles due to their different social backgrounds. The film explores the themes of love, family, and societal pressure.');
INSERT INTO bollywood_movies (movie_id, movie_name, main_leads, plot_description) VALUES (389, 'Ishqiya', 'Naseeruddin Shah, Vidya Balan, Arshad Warsi', 'A dark comedy thriller about two small-time criminals who get involved with a widow, leading to love, betrayal, and deceit. Naseeruddin Shah plays a pivotal role as one of the criminals in this unpredictable film.');
